import { GeofenceService } from './geofences';
import { SpeedAlertService } from './speed-alerts';
import { ClientService } from './clients';

export class ApplicationContext {
  public readonly geofences:GeofenceService;
  public readonly speedAlerts:SpeedAlertService;
  public readonly clients:ClientService;
  public applicationAccessToken:string;

  constructor(
    public applicationId:string,
    public secret:string) {
      this.geofences = new GeofenceService(this);
      this.speedAlerts = new SpeedAlertService(this);
      this.clients = new ClientService(this);
    }

}
