import { IReaction } from '../reactions/';

export interface ITransition {
    reaction: IReaction;
    constructor(reactions: IReaction[]): ITransition;
}

export type GeofenceTransition = EnterTransition | ExitTransition | DwellTransition;

export class EnterTransition { 
    constructor(public reactions: Array<IReaction>) { }
}

export class ExitTransition { 
    constructor(public reactions: Array<IReaction>) { }
}

export class DwellTransition { 
    constructor(public reactions: Array<IReaction>, public duration: number) { }
}

export class SpeedTransition { 
    constructor(public reactions: Array<IReaction>, public speed: number) { }
}
